Bash(convert-to-hk.mjs << 'EOF'
import * as OpenCC from 'opencc-js';
import * as fs from 'fs';
import * as path from 'path';

const cnToHk = OpenCC.Converter\({ from: 'cn', to: 'hk' }\);

const files = [
  'app/pages/entries/index.vue',
  'app/pages/login.vue',
  'app/pages/register.vue',
  'app/pages/review/index.vue',
  'app/pages/histories/index.vue',
  'app/components/layout/AppHeader.vue',
  'app/components/layout/AppSidebar.vue',
  'app/components/entries/EntryModal.vue',
  'app/composables/useAuth.ts',
  'server/utils/auth.ts',
  'server/utils/ai.ts',
  'server/utils/Entry.ts',
  'server/middleware/auth.ts',
  'nuxt.config.ts'
];

function convertFile\(filePath\) {
  const fullPath = path.resolve\(filePath\);
  if \(!fs.existsSync\(fullPath\)\) {
    console.log\(`Skipping ${filePath} - not found`\);
    return;
  }

  const content = fs.readFileSync\(fullPath, 'utf-8'\);
  const converted = cnToHk\(content\);

  if \(content !== converted\) {
    fs.writeFileSync\(fullPath, converted, 'utf-8'\);
    console.log\(`Converted: ${filePath}`\);
  } else {
    console.log\(`No changes: ${filePath}`\);
  }
}

files.forEach\(convertFile\);
console.log\('Done!'\);
EOF)

