  console.log\('PRD file needs conversion - saving...'\);
  fs.writeFileSync\('JyutCollab-v2-PRD.md', converted, 'utf-8'\);
  console.log\('Converted: JyutCollab-v2-PRD.md'\);
} else {
  console.log\('PRD file already in HK Traditional'\);
}
